/**
 * etik-inscription.js
 * jQuery-based handler for inscription / login modal and AJAX flow.
 *
 * Requires:
 * - WP_ETIK_AJAX global localized with { ajax_url, nonce }
 * - HTML modal structure generated by Divi_Module::render() (classes/ids used below)
 *
 * Features:
 * - Open/close modal (buttons with .etik-formation-btn and data-modal)
 * - Tabs (buttons .etik-tab switching .etik-panel)
 * - Submit inscription (.etik-insc-form) and login (.etik-login-form) via admin-ajax
 * - Nonce inclusion, client feedback, simple client validation
 * - Optional page reload after successful login
 */

(function($){
  'use strict';

  var ajaxUrl = (window.WP_ETIK_AJAX && WP_ETIK_AJAX.ajax_url) ? WP_ETIK_AJAX.ajax_url : '/wp-admin/admin-ajax.php';
  var globalNonce = (window.WP_ETIK_AJAX && WP_ETIK_AJAX.nonce) ? WP_ETIK_AJAX.nonce : '';
  var hcaptchaSiteKey = (window.WP_ETIK_AJAX && WP_ETIK_AJAX.hcaptcha_sitekey) ? WP_ETIK_AJAX.hcaptcha_sitekey : '';

  // Short helpers
  function $modal() { return $('#etik-global-modal'); }
  function find($context, sel) { return $context.find(sel); }
  function showFeedback($m, type, msg) {
    var $fb = find($m, '.etik-feedback');
    if (!$fb.length) {
      $fb = $('<div class="etik-feedback" aria-live="polite"></div>').appendTo(find($m, '.etik-modal-content').first());
    }
    $fb.removeClass('success error').addClass(type || '').text(msg || '').show();
  }
  function clearFeedback($m) {
    var $fb = find($m, '.etik-feedback');
    if ($fb.length) { $fb.hide().removeClass('success error').text(''); }
  }

  // hCaptcha widget id storage key on modal data
  var HCAPTCHA_WIDGET_KEY = 'etik_hcaptcha_widget';

  // Initialize hCaptcha if sitekey present and hcaptcha global available
  function initHCaptcha($m) {
    if (!hcaptchaSiteKey) return;
    if (typeof hcaptcha === 'undefined') return;
    // find placeholder
    var placeholder = $m.find('.etik-hcaptcha-placeholder')[0];
    if (!placeholder) return;
    var existing = $m.data(HCAPTCHA_WIDGET_KEY);
    if (!existing) {
      try {
        var widgetId = hcaptcha.render(placeholder, { sitekey: hcaptchaSiteKey });
        $m.data(HCAPTCHA_WIDGET_KEY, widgetId);
      } catch (err) {
        // silent fail
      }
    } else {
      try { hcaptcha.reset(existing); } catch(e) {}
    }
  }

  // Open modal and populate dynamic data
  $(document).on('click', '.etik-formation-btn', function(e){
    e.preventDefault();
    var $btn = $(this);
    var eventId = $btn.data('event') || $btn.attr('data-event') || '';
    var title = $btn.data('title') || $btn.attr('data-title') || '';
    var $m = $modal();
    if (!$m.length) return;

    // Populate hidden field and title
    find($m, 'input[name="event_id"]').val(eventId);
    var $titleEl = $('#etik-modal-title');
    if ($titleEl.length) {
      $titleEl.text(title ? ('Inscription : ' + title) : 'Inscription à la formation');
    }

    // Reset form and feedback
    var $form = find($m, 'form.etik-insc-form');
    if ($form.length) $form[0].reset();
    clearFeedback($m);

    // Initialize or reset hCaptcha widget
    if (hcaptchaSiteKey) initHCaptcha($m);

    // Show modal
    $m.attr('aria-hidden','false');

    // Focus first input
    setTimeout(function(){
      var $first = $m.find('input, textarea, button, select').filter(':visible').first();
      if ($first.length) $first.focus();
    }, 40);
  });

  // Close modal handlers (backdrop, close button, data-modal-close attribute)
  $(document).on('click', '.etik-modal-backdrop, .etik-modal-close, [data-modal-close]', function(e){
    e.preventDefault();
    var $m = $(this).closest('.etik-modal');
    if ($m.length) {
      closeModal($m);
    }
  });

  function closeModal($m) {
    try {
      // reset hcaptcha if present
      var widgetId = $m.data(HCAPTCHA_WIDGET_KEY);
      if (typeof hcaptcha !== 'undefined' && widgetId !== undefined) {
        try { hcaptcha.reset(widgetId); } catch(e) {}
      }
    } catch(e){}
    $m.attr('aria-hidden','true');
  }

  // Close on ESC
  $(document).on('keydown', function(e){
    if (e.key === 'Escape' || e.keyCode === 27) {
      var $m = $modal();
      if ($m.length && $m.attr('aria-hidden') === 'false') closeModal($m);
    }
  });

  // Minimal focus trap while modal open
  $(document).on('keydown', '.etik-modal[aria-hidden="false"]', function(e){
    if (e.key !== 'Tab' && e.keyCode !== 9) return;
    var $m = $(this);
    var focusable = $m.find('a[href], area[href], input:not([disabled]), select:not([disabled]), textarea:not([disabled]), button:not([disabled]), [tabindex]').filter(':visible');
    if (!focusable.length) return;
    var first = focusable.first()[0];
    var last = focusable.last()[0];
    if (e.shiftKey) {
      if (document.activeElement === first) {
        e.preventDefault();
        last.focus();
      }
    } else {
      if (document.activeElement === last) {
        e.preventDefault();
        first.focus();
      }
    }
  });

  // Form submit for inscription (single panel)
  $(document).on('submit', '.etik-insc-form', function(e){
    e.preventDefault();
    var $form = $(this);
    var $m = $form.closest('.etik-modal');
    clearFeedback($m);

    // Grab values
    var first_name = $.trim($form.find('input[name="first_name"]').val() || '');
    var last_name = $.trim($form.find('input[name="last_name"]').val() || '');
    var email = $.trim($form.find('input[name="email"]').val() || '');
    var phone = $.trim($form.find('input[name="phone"]').val() || '');
    var desired_domain = $.trim($form.find('input[name="desired_domain"]').val() || '');
    var has_domain = $form.find('input[name="has_domain"]').is(':checked') ? 1 : 0;
    var event_id = $.trim($form.find('input[name="event_id"]').val() || '');

    // Client validation
    if (!first_name || !email || !phone || !event_id) {
      showFeedback($m, 'error', 'Veuillez remplir les champs obligatoires : Prénom, E-mail et Téléphone.');
      return;
    }
    var emailRE = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRE.test(email)) {
      showFeedback($m, 'error', 'Adresse e-mail invalide.');
      return;
    }

    var postData = {
      action: $form.find('input[name="action"]').val() || 'lwe_create_checkout',
      first_name: first_name,
      last_name: last_name,
      email: email,
      phone: phone,
      desired_domain: desired_domain,
      has_domain: has_domain,
      event_id: event_id,
      nonce: globalNonce
    };

    // hCaptcha: include response token if widget present
    if (hcaptchaSiteKey && typeof hcaptcha !== 'undefined') {
      var widgetId = $m.data(HCAPTCHA_WIDGET_KEY);
      if (widgetId !== undefined) {
        var token = hcaptcha.getResponse(widgetId);
        if (!token) {
          showFeedback($m, 'error', 'Veuillez compléter le captcha.');
          return;
        }
        postData['h-captcha-response'] = token;
      }
    }

    // Disable submit UI
    var $submit = $form.find('button[type="submit"]').prop('disabled', true);
    showFeedback($m, '', 'Envoi en cours...');

    $.post(ajaxUrl, postData, function(resp){
      $submit.prop('disabled', false);

      

      if (resp && resp.success) {

        var data = resp.data || {};

        // Si checkout_url présent → rediriger
        if (data.checkout_url) {
            showFeedback($m, 'success', '<div style="display:flex;align-items:center;gap:8px;"><span class="dashicons dashicons-yes"></span> Redirection vers le paiement...</div>');
            //showFeedback($m, 'success', 'Redirection vers le paiement...');
            setTimeout(function(){
                window.location.href = data.checkout_url;
            }, 1500);
            return;
        }

        // Sinon → afficher message et fermer modal
        var message = data.message || 'Inscription enregistrée. Vérifiez votre e-mail pour confirmer.';
        showFeedback($m, 'success', message);
        setTimeout(function(){ closeModal($m); }, 2200);

        /*showFeedback($m, 'success', resp.data && resp.data.message ? resp.data.message : 'Inscription enregistrée. Vérifiez votre e-mail pour confirmer.');
        // auto close after short delay
        setTimeout(function(){ closeModal($m); }, 2200);*/
      } else {
        var msg = 'Erreur';
        if (resp && resp.data && resp.data.message) msg = resp.data.message;
        showFeedback($m, 'error', msg);
        // reset hcaptcha if present so user can try again
        try {
          var w = $m.data(HCAPTCHA_WIDGET_KEY);
          if (typeof hcaptcha !== 'undefined' && w !== undefined) { hcaptcha.reset(w); }
        } catch(e){}
      }

    }, 'json').fail(function(){
      $submit.prop('disabled', false);
      showFeedback($m, 'error', 'Erreur réseau. Réessayez.');
      try {
        var w2 = $m.data(HCAPTCHA_WIDGET_KEY);
        if (typeof hcaptcha !== 'undefined' && w2 !== undefined) { hcaptcha.reset(w2); }
      } catch(e){}
    });
  });

  // Ensure modal hidden on document ready
  $(function(){
    var $m = $modal();
    if ($m.length) {
      $m.attr('aria-hidden', 'true');
      // init hcaptcha placeholder if sitekey and hcaptcha already loaded
      if (hcaptchaSiteKey && typeof hcaptcha !== 'undefined') {
        // We don't render widget until modal opens, but if you prefer pre-render uncomment below
        // initHCaptcha($m);
      }
    }
  });

})(jQuery);
